import { Helmet } from 'react-helmet';

const Games = ({name, setName, game, setGame}) => {
  const sendGameRequest = (e) => {
    e.preventDefault();

    const link = document.createElement('a');
    link.href = `mailto:eliasbruhn123@gmail.com?subject=Regarding%20new%20game%20request:%20${game}&body=Hello,%20my%20name%20is%20${name},%20and%20I%20would%20like%20to%20request%20rules%20for%20"${game}"%20on%20your%20Game%20Rules%20site.`;
    // link.click();

    document.querySelector('.content').innerHTML += `<div style="
    background: lawngreen;
    ">You successfully requested rules for "${game}"</div>`;

    setGame('');
    setName('');

    setInterval(() => {
      // window.location.reload();
    }, 5000);
  }

  return (
    <>
      <Helmet>
        <title>Games | Game Rules</title>
      </Helmet>
      <h1>Games</h1>
      <form onSubmit={sendGameRequest}>
        <h2>Submit a game request:</h2>
        <label htmlFor="name">Your name</label>
        <input required type="name" name="name" placeholder="Your name.." value={name} onChange={(e) => setName(e.target.value)} />
        <label htmlFor="game">Game</label>
        <input required type="text" name="game" placeholder="Game you want to request.." value={game} onChange={(e) => setGame(e.target.value)} />
        <input type="submit" value="Submit" className="submit" />
      </form>
    </>
  );
};

export default Games;
