import { Helmet } from 'react-helmet';
var sendemail = require('sendemail')

const Games = ({name, setName, email, setEmail, game, setGame}) => {
  const sendGameRequest = (e) => {
    e.preventDefault();

    console.log('name', name);
    console.log('email', email);
    console.log('game', game);


    var sendEmail = sendemail.email;

var person = {
  name,
  email,
  subject: game
}

sendEmail('welcome', person, function(error, result){
  console.log(' - - - - - - - - - - - - - - - - - - - - -> email sent: ');
  console.log(result);
  console.log(' - - - - - - - - - - - - - - - - - - - - - - - - - - - -')
})

  }

  return (
    <>
      <Helmet>
        <title>Games | Game Rules</title>
      </Helmet>
      <h1>Games</h1>
      <h2>Submit a game request:</h2>
      <form onSubmit={sendGameRequest}>
        <input type="name" name="name" placeholder="Your name.." value={name} onChange={(e) => setName(e.target.value)} />
        <input type="email" name="email" placeholder="Your email.." value={email} onChange={(e) => setEmail(e.target.value)} />
        <input type="text" name="game" placeholder="Game you want to request.." value={game} onChange={(e) => setGame(e.target.value)} />
        <input type="submit" value="Submit" />
      </form>
    </>
  );
};

export default Games;
