import { Helmet } from 'react-helmet';
import { useState } from 'react';

const Games = () => {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [game, setGame] = useState('');

  const sendGameRequest = (e) => {
    e.preventDefault();

    console.log('name', name);
    console.log('email', email);
    console.log('game', game);
    // const data = {name, email, game};

    // fs.writeFile('data.json', JSON.stringify(data), 'utf8', (err, data) => {
    //   if (err) {
    //     console.err('Error: ', err);
    //   } else {
    //     console.log('Data successfully stored in `data.json`');
    //   }
    // })

    const handleSaveToPC = jsonData => {
      const fileData = JSON.stringify(jsonData);
      const blob = new Blob([fileData], {type: "text/plain"});
      const url = URL.createObjectURL(blob);
      const link = document.createElement('a');
      link.download = 'filename.json';
      link.href = url;
      link.click();
    }

    const json = {name, email, game};
    handleSaveToPC(json);
  }

  return (
    <>
      <Helmet>
        <title>Games | Game Rules</title>
      </Helmet>
      <h1>Games</h1>
      <h2>Submit a game request:</h2>
      <form onSubmit={sendGameRequest}>
        <input type="name" name="name" placeholder="Your name.." value={name} onChange={(e) => setName(e.target.value)} />
        <input type="email" name="email" placeholder="Your email.." value={email} onChange={(e) => setEmail(e.target.value)} />
        <input type="text" name="game" placeholder="Game you want to request.." value={game} onChange={(e) => setGame(e.target.value)} />
        <input type="submit" value="Submit" />
      </form>
    </>
  );
};

export default Games;
